class RandomizedSet:
    def __init__(self) -> None:
        self.mp: Dict[int, int] = {}
        self.arr: List[int] = []

    def insert(self, val: int) -> bool:
        if val in self.mp:
            return False
        self.mp[val] = len(self.arr)
        self.arr.append(val)
        return True

    def remove(self, val: int) -> bool:
        if val not in self.mp:
            return False

        # Index of element to delete and index of last element
        idx_to_delete = self.mp[val]
        last_idx = len(self.arr) - 1
        last_val = self.arr[last_idx]

        # Move last element into the spot of the element to delete
        self.arr[idx_to_delete], self.arr[last_idx] = (
            self.arr[last_idx],
            self.arr[idx_to_delete],
        )
        self.mp[last_val] = idx_to_delete

        # Remove the last element from the list and delete the map entry
        self.arr.pop(-1)
        del self.mp[val]

        return True

    def getRandom(self) -> int:
        return random.choice(self.arr)

