        nums.sort()
        ans = set()

        for i in range(len(nums)):
            if i != 0 and nums[i] == nums[i - 1]:
                # Avoid duplicates
                continue

            j, k = i + 1, len(nums) - 1

            while j < k:
                sm = nums[i] + nums[j] + nums[k]

                if sm == 0:
                    ans.add(
                        (
                            nums[i],
                            nums[j],
                            nums[k],
                        )
                    )
                    j += 1
                    k -= 1
                    while nums[j] == nums[j - 1] and j < k:
                        j += 1

                    while nums[k] == nums[k + 1] and j < k:
                        k -= 1

                elif sm > 0:
                    k -= 1

                elif sm < 0:
                    j += 1

        return list(ans)

